$(function(){function selectCenter(){$("select").each(function(){var select=$(this),width=null,left=null;width=parseInt($('<span class="calcSpan"></span>').html(this["selectedOptions"][0].innerText).appendTo(document.body).css({font:select.css("font"),display:"none"}).css("width"));left=Math.round(parseInt((parseInt(select.css("width"))-width-22)/2));if(left>=0){select.css("textIndent",left+"px")}$(".calcSpan").remove()})}$("select").change(function(){selectCenter()});selectCenter()});