$(function(){function selectCenter(){$("select").each(function(){var select=$(this),width=null,meHalf=null;width=parseInt($('<span class="calcSpan"></span>').html(this["selectedOptions"][0].innerText).appendTo(document.body).css({font:select.css("font"),display:"none"}).css("width"));meHalf=Math.floor(width/2);select.css("textIndent","calc(50% - "+meHalf+"px)");$(".calcSpan").remove()})}$("select").change(function(){selectCenter()});selectCenter()});